    private void comfimActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_comfimActionPerformed
        //confim
        int choice = JOptionPane.showConfirmDialog(this,"ban cos muon xoa hay k !");
        if(JOptionPane.YES_OPTION==choice){
            JOptionPane.showMessageDialog(this,"ban da chon yes");
        }else  if(JOptionPane.NO_OPTION==choice){
            JOptionPane.showMessageDialog(this,"ban da chon no !");
        }else{
            JOptionPane.showMessageDialog(this,"ban da chon cancel");
        }
                
    }//GEN-LAST:event_comfimActionPerformed

